import { react, useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import CallToAction from '../components/CallToAction'
import PostCard from '../components/PostCard'

const HomePage = () => {
  const [posts, setPosts] = useState([])

  useEffect(() => {
    const fetchPosts = async () => {
      const res = await fetch('/api/posts/getposts')
      const data = await res.json()
      setPosts(data.posts)
    }
    fetchPosts()
  }, [])

  return (
    <div>
      <div className='flex flex-col gap-6 p-28 px-3 max-w-6xl mx-auto'>
        <h1 className='text-3xl font-bold lg:text-6xl'>Welcome to Es Lanka Travels</h1>
        <p className='text-gray-500 text-md sm:text-sm'>
          Endless beaches, timeless ruins, welcoming people, oodles of elephants,
          rolling surf, cheap prices, fun trains, famous tea and flavorful food
          make Sri Lanka irresistible.
        </p>
        <Link to='/search' className='text-xs sm:text-sm text-teal-500 font-bold hover:underline'>
          View all posts
        </Link>
      </div>

      <div className='p-3 bg-amber-100 dark:bg-slate-700'>
        <CallToAction />
      </div>

      <div className='max-w-6xl mx-auto p-3 flex flex-col gap-8 py-7'>
        {posts && posts.length > 0 && (
          <div className='flex flex-col gap-6'>
            <h2 className='text-2xl font-semibold text-center'>Recent Posts</h2>
            <div className='grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4'>
              {posts.map((post) => (
                <PostCard key={post._id} post={post} />
              ))}
            </div>
            <Link to={"/search"} className='text-lg text-teal-500 hover:underline text-center'>
              View all posts
            </Link>
          </div>
        )}
      </div>

      {/* Top Destinations Section */}
      <section className='bg-white dark:bg-slate-800 py-16'>
        <div className='max-w-6xl mx-auto text-center px-4'>
          <h2 className='text-3xl font-bold mb-10'>Top Places to Visit in Sri Lanka</h2>
          <div className='grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6'>
            {[
              {
                name: 'Sigiriya Rock Fortress',
                img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnltZppp7aIzTnYbNm8x7KJIIJn_sqmMw88g&s',
                desc: 'Climb to the top of this ancient marvel for breathtaking views.',
              },
              {
                name: 'Ella Nine Arch Bridge',
                img: 'data:image/jpeg;base64,',
                desc: 'Charming train spot surrounded by lush greenery and tea estates.',
              },
              {
                name: 'Yala National Park',
                img: 'data:image/jpeg;base64,',
                desc: 'Explore wildlife with thrilling safaris in this famous sanctuary.',
              },
            ].map((place, idx) => (
              <div key={idx} className='bg-teal-700 rounded-lg shadow-md overflow-hidden'>
                <img src={place.img} alt={place.name} className='h-48 w-full object-cover' />
                <div className='p-4'>
                  <h3 className='font-semibold text-lg'>{place.name}</h3>
                  <p className='text-sm text-black mt-1'>{place.desc}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Why Travel With Us Section */}
      <section className='bg-teal-50 dark:bg-slate-700 py-16'>
        <div className='max-w-6xl mx-auto text-center px-4'>
          <h2 className='text-3xl font-bold mb-10'>Why Travel With Es Lanka?</h2>
          <div className='grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8'>
            {[
              {
                title: 'Tailor-Made Itineraries',
                icon: '🌍',
                desc: 'We craft journeys based on your unique travel style and interests.',
              },
              {
                title: 'Local Expertise',
                icon: '🧭',
                desc: 'Guided by locals, you’ll explore authentic Sri Lanka beyond the tourist path.',
              },
              {
                title: '24/7 Support',
                icon: '📞',
                desc: 'We’re with you from start to finish, anytime you need us.',
              },
            ].map((item, idx) => (
              <div key={idx} className='bg-white dark:bg-slate-800 p-6 rounded-lg shadow text-center'>
                <div className='text-4xl mb-3'>{item.icon}</div>
                <h3 className='text-xl font-semibold'>{item.title}</h3>
                <p className='text-sm text-gray-600 mt-2 dark:text-gray-300'>{item.desc}</p>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  )
}

export default HomePage
